import React, { Component } from 'react';
import './App.css';
import {
  Alert, Container, Row, Col, Button
} from 'reactstrap';
import randomstring from 'randomstring';

import TaskForm from './components/TaskForm';
import Control from './components/Control';
import TaskList from './components/TaskList';

import { connect } from 'react-redux';
import * as actions from './actions';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      taskEditing: null,
      filter: {
        filterName: '',
        filterStatus: -1
      },
      keyword: '',
      softBy: 'name',
      softValue: 1
    }
  }

  //Dữ liệu mẫu
  createSampleData = () => {
    let tasks = [
      {
        id: randomstring.generate(5),
        namework: 'Học HTML',
        status: true
      },
      {
        id: randomstring.generate(5),
        namework: 'Học CSS',
        status: true
      },
      {
        id: randomstring.generate(5),
        namework: 'Học ReactJS Basic',
        status: false
      }
    ];
    localStorage.setItem('tasks', JSON.stringify(tasks));
    this.setState({
      tasks: tasks
    });
  }

  //Đóng/mở form
  onToggleForm  = () => {
    this.props.onToggleForm();
  }

  onShowForm = () => {
    this.setState({
      isDisplayForm: true
    });
  }

  //Chỉnh sửa form
  editTask = (id) => {
    this.onShowForm();
    let { tasks } = this.state;
    var index = this.findIndex(id);
    var taskEditing = tasks[index];
    this.setState({
      taskEditing
    });



  }

  //Cập nhật status
  onUpdateStatus = (id) => {
    var { tasks } = this.state;
    var index = this.findIndex(id);
    if( index !== -1){
      tasks[index].status = !tasks[index].status
    }
    this.setState({
      tasks
    });
    localStorage.setItem('tasks',JSON.stringify(tasks));
  }


  //Xóa phần tử
  onDelete = (id) => {
    var { tasks } = this.state;
    var index = this.findIndex(id);
    tasks.splice(index, 1)
    this.setState({
      tasks
    });
    localStorage.setItem('tasks',JSON.stringify(tasks));
  }

  //Tìm index
  findIndex = (id) => {
    var { tasks } = this.state;
    let result = -1;
    tasks.forEach( (task,index) => {
      if(task.id === id){
        return result = index; 
      }
    });
    return result;
  }

  //Filter
  onFilter = (filterName, filterStatus) => {
    let status = parseInt(filterStatus,10);
    this.setState({
      filter: {
        filterName: filterName.toLowerCase(),
        filterStatus : status
      }
    });
  }
  //Search
  onSearch = (keyword) => {
    if(keyword){
      this.setState({
        keyword
      });
    }

  }
  //Soft
  onSoft = (softBy, softValue) => {
    this.setState({
          softBy: softBy,
          softValue: softValue
    });
  }

  render() {
    var { 
      taskEditing, 
      // filter, 
      // keyword, 
      softBy, 
      softValue 
    } = this.state;

    var { isDisplayForm } = this.props;
    // if(filter && filter.filterName){
    //   tasks = tasks.filter(task => {
    //     return task.namework.toLowerCase().indexOf(filter.filterName) !== -1
    //   });
    // }

    // tasks = tasks.filter(task => {
    //     if(filter.filterStatus === -1 ){
    //       return task
    //     }else{
    //       return task.status === (filter.filterStatus === 1 ? true : false)
    //     }  
    // });

    // if(keyword){
    //   tasks = tasks.filter(task => {
    //     return task.namework.toLowerCase().indexOf(keyword) !== -1
    //   });
    // }
    //Soft
    // if(softBy === 'name'){
    //   tasks = tasks.sort((a, b) => {
    //     var nameA = a.namework.toUpperCase(); // bỏ qua hoa thường
    //     var nameB = b.namework.toUpperCase(); // bỏ qua hoa thường
    //     if (nameA < nameB) {
    //       return -softValue;
    //     }
    //     if (nameA > nameB) {
    //       return softValue  ;
    //     }
    //     // name trùng nhau
    //     return 0;
    //   });
    // }else{
    //   tasks = tasks.sort((a, b) => {
    //     var nameA = a.status;
    //     var nameB = b.status; 
    //     if (nameA < nameB) {
    //       return softValue;
    //     }
    //     if (nameA > nameB) {
    //       return -softValue  ;
    //     }
    //     // name trùng nhau
    //     return 0;
    //   });
    // }

    let elementTaskForm = isDisplayForm 
          ? <TaskForm  
              onSubmit={ this.onSubmit }
              task = {taskEditing}
            /> 
          : '';
    return (
      <div className="App">
        <Alert color="primary">
          <h1 className="title-app">Quản lý công việc</h1>
        </Alert>
        <Container>
          <Row>
            <Col md={ isDisplayForm ? "4" : "0"}>
              { elementTaskForm }
            </Col>
            <Col md={ isDisplayForm ? "8" : "12" }>
              <Col md="12" className="p-0 mb-2">
                <Button color="primary" onClick={ this.onToggleForm }>Thêm công việc</Button>{' '}
                <Button color="danger" onClick={this.createSampleData}>Tạo dữ liệu mẫu</Button>{' '}
              </Col>
              <Control 
                onSearch={this.onSearch}
                onSoft={this.onSoft}
                softBy={softBy}
                softValue={softValue}
              />
              <TaskList 
                // tasks={ tasks } 
                editTask={this.editTask}
                onUpdateStatus = {this.onUpdateStatus}
                onDelete = {this.onDelete}
                onFilter = {this.onFilter}
              />
            </Col>
          </Row>
        </Container>
      </div>
    );
  }
}

let mapStateToProps = (state) => {
  return {
    isDisplayForm: state.isDisplayForm
  };
};

let mapDispatchToProps = (dispatch,action) => {
  return {
    onToggleForm: () => {
      dispatch(actions.toggleForm());
    },
    onOpenForm: () => {
      dispatch(actions.openForm());
    }
  };
};

export default connect(mapStateToProps,mapDispatchToProps)(App);
