import React, { Component } from 'react';

import {
  Card, CardHeader, CardBody, CardFooter,
  Form, FormGroup, Label, Input, Button
} from 'reactstrap';

class TaskForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      namework: '',
      status: false
    }
  }

  onChange = (event) => {
    let target = event.target;
    let namework = target.namework;
    let value = target.value;
    this.setState({
      [namework] : value
    });
  }

  // onSubmit = (event) =>{
  //   console.log(this.state);
  // }

  render() {
    let { namework, status } = this.state;
    return (
      <Card>
        <CardHeader>Thêm công việc</CardHeader>
        <Form onSubmit={this.onSubmit}>
          <CardBody>
            <FormGroup>
              <Label for="namework">Tên công việc:</Label>
              <Input
                type="text"
                placeholder=""
                id="namework"
                name="namework"
                value={namework}
                onChange={this.onChangeForm}
              />
            </FormGroup>
            <FormGroup>
              <Label for="statuswork">Trạng thái</Label>
              <Input
                type="select"
                id="statuswork"
                name="statuswork"
                value={status}
                onChange={this.onChangeForm}
              >
                <option value={true}>Kích hoạt</option>
                <option value={false}>Ẩn</option>
              </Input>
            </FormGroup>
          </CardBody>
          <CardFooter>
            <Button color="primary">Lưu lại</Button>{' '}
            <Button>Hủy bỏ</Button>
          </CardFooter>
        </Form>
      </Card>
    );
  }
}

export default TaskForm;
