import React, { Component } from 'react';
import './App.css';
import {
  Alert, Container, Row, Col,
  Card, CardHeader, CardBody, CardText, CardTitle, CardFooter,
  Form, FormGroup, Label, Input, FormText, Button
} from 'reactstrap';

import TaskForm from './components/TaskForm';
import Control from './components/Control';
import TaskList from './components/TaskList'

class App extends Component {
  render() {
    return (
      <div className="App">
        <Alert color="primary">
          <h1 className="title-app">Quản lý công việc</h1>
        </Alert>
        <Container>
          <Row>
            <Col md="4">
              <TaskForm />
            </Col>
            <Col md="8">
              <Col md="4" className="p-0">
                <Button color="primary">Thêm công việc</Button>{' '}
              </Col>
              <Control/>
              <TaskList/>
            </Col>
          </Row>
        </Container>
      </div>
    );
  }
}

export default App;
