import React, { Component } from 'react';
import {
    Table
} from 'reactstrap';

import TaskItem from './TaskItem';

class TaskList extends Component {
    log(){
        console.log('abc');
        console.log(this.props.isDisplayForm);
    }
    render() {
        let { tasks, isDisplayForm } = this.props;
        let elementTask = tasks.map((task, index)=>{
            return <TaskItem key={task.id} index={index} task={task} isDisplayForm={isDisplayForm}/>
        });
        return (
            <Table bordered>
                <thead>
                    <tr>
                        <th onLoad={()=>this.log()} >STT</th>
                        <th>Tên công việc</th>
                        <th>Trạng thái</th>
                        <th>Hành động</th>
                    </tr>
                </thead>
                <tbody>
                    { elementTask }
                </tbody>
            </Table>
        );
    }
}

export default TaskList;
